{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:07:56.49807Z\",\"iopub.execute_input\":\"2021-12-11T03:07:56.498821Z\",\"iopub.status.idle\":\"2021-12-11T03:08:05.30159Z\",\"shell.execute_reply.started\":\"2021-12-11T03:07:56.498774Z\",\"shell.execute_reply\":\"2021-12-11T03:08:05.300338Z\"}}\n!pip install pmdarima\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:05.304247Z\",\"iopub.execute_input\":\"2021-12-11T03:08:05.304663Z\",\"iopub.status.idle\":\"2021-12-11T03:08:05.382175Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:05.304611Z\",\"shell.execute_reply\":\"2021-12-11T03:08:05.381098Z\"}}\n# This Python 3 environment comes with many helpful analytics libraries installed\n# It is defined by the kaggle/python Docker image: https://github.com/kaggle/docker-python\n# For example, here's several helpful packages to load\n\nimport numpy as np # linear algebra\nimport pandas as pd # data processing, CSV file I/O (e.g. pd.read_csv)\nfrom datetime import datetime\nimport matplotlib\nimport matplotlib.pylab as plt\nimport matplotlib.pyplot as plt2\nmatplotlib.style.use('seaborn')\n%matplotlib inline\nfrom matplotlib.pylab import rcParams\nrcParams['figure.figsize'] = 15, 5\nfrom plotly.graph_objs import *\nfrom plotly.offline import init_notebook_mode, iplot, iplot_mpl\ninit_notebook_mode()\nfrom tqdm import tqdm\nimport statsmodels.api as sm\nfrom pmdarima.arima import auto_arima\n\n#muligens fjerne\nfrom statsmodels.tsa.stattools import adfuller\nfrom statsmodels.graphics.tsaplots import plot_acf, plot_pacf\nfrom statsmodels.tsa.arima_model import ARIMA\nimport warnings\nwarnings.filterwarnings('ignore')\n\n# Input data files are available in the read-only \"../input/\" directory\n# For example, running this (by clicking run or pressing Shift+Enter) will list all files under the input directory\n\nimport os\n#for dirname, _, filenames in os.walk('/kaggle/input'):\n    #for filename in filenames:\n        #print(os.path.join(dirname, filename))\n        \n\n# You can write up to 20GB to the current directory (/kaggle/working/) that gets preserved as output when you create a version using \"Save & Run All\" \n# You can also write temporary files to /kaggle/temp/, but they won't be saved outside of the current session\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:05.383712Z\",\"iopub.execute_input\":\"2021-12-11T03:08:05.383954Z\",\"iopub.status.idle\":\"2021-12-11T03:08:05.587294Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:05.383925Z\",\"shell.execute_reply\":\"2021-12-11T03:08:05.586359Z\"}}\npath = '/kaggle/input/price-volume-data-for-all-us-stocks-etfs/Stocks/dis.us.txt'\n#path = '/kaggle/input/price-volume-data-for-all-us-stocks-etfs/Stocks/acgl.us.txt'\ndateparse = lambda dates: datetime.strptime(dates, '%Y-%m-%d')\ndf = pd.read_csv(path,sep=',', index_col='Date', parse_dates=['Date'], date_parser=dateparse).fillna(0)\n\ndf['Close']\ndf['Close_diff'] = df['Close']-df.shift()['Close']\ndf = df[['Close', 'Close_diff']]\ndf = df.dropna()\ndf\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:05.591247Z\",\"iopub.execute_input\":\"2021-12-11T03:08:05.591495Z\",\"iopub.status.idle\":\"2021-12-11T03:08:05.835829Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:05.591467Z\",\"shell.execute_reply\":\"2021-12-11T03:08:05.835117Z\"}}\n#plot close price\nplt.figure(figsize=(10,6))\nplt.grid(True)\nplt.xlabel('Date')\nplt.ylabel('Close Prices')\nplt.plot(df['Close'])\nplt.title('Financial instrument closing price')\nplt.show()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:05.837255Z\",\"iopub.execute_input\":\"2021-12-11T03:08:05.837777Z\",\"iopub.status.idle\":\"2021-12-11T03:08:06.204844Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:05.837732Z\",\"shell.execute_reply\":\"2021-12-11T03:08:06.204129Z\"}}\n#split into test and train sets\n\n#Choose to split on percentage\n\n#test_size = 0.1\n#train_data = df[:int(len(df['Close'])*(1-test_size))]\n#test_data = df[-int(len(df['Close'])*test_size):]\n\n#Choice to split on date range\n\ntrain_data = df['2015-07-01':'2017-10-09']\ntest_data = df['2017-10-10':]\n\nplt.figure(figsize=(10,6))\nplt.grid(True)\nplt.xlabel('Dates')\nplt.ylabel('Closing Prices')\nplt.plot(train_data['Close'], 'black', label='Train data')\nplt.plot(test_data['Close'], 'red', label='Test data')\nplt.legend()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:06.205828Z\",\"iopub.execute_input\":\"2021-12-11T03:08:06.20657Z\",\"iopub.status.idle\":\"2021-12-11T03:08:06.308013Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:06.206526Z\",\"shell.execute_reply\":\"2021-12-11T03:08:06.307254Z\"}}\niplot([Scatter(x=train_data.index, y=train_data['Close_diff'])])\niplot([Histogram(x=train_data['Close_diff'])])\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:10:14.045528Z\",\"iopub.execute_input\":\"2021-12-11T03:10:14.045876Z\",\"iopub.status.idle\":\"2021-12-11T03:10:14.725633Z\",\"shell.execute_reply.started\":\"2021-12-11T03:10:14.045842Z\",\"shell.execute_reply\":\"2021-12-11T03:10:14.724678Z\"}}\n#Check if time series is staionary\ndef timeseries_stationary(ts):\n    #Determing rolling statistics\n    rolmean = ts.rolling(12).mean()\n    rolstd = ts.rolling(12).std()\n    plt.plot(ts, color='blue',label='Original')\n    plt.plot(rolmean, color='red', label='Mean')\n    plt.plot(rolstd, color='black', label = 'Std')\n    plt.legend(loc='best')\n    plt.title('Mean and Standard Deviation')\n    plt.show(block=False)\n    \n    print(\"ADF Result\")\n    adft = adfuller(ts,autolag='AIC')\n    result = adft[0:4]\n    output = pd.Series(data = result,index=['ADF Statistic','p-value','Lags used','Observations used'])\n    for index, item in adft[4].items():\n        output['critical value: %s'%index] =  item\n    print(output)\n    \ntimeseries_stationary(train_data['Close'])\ntimeseries_stationary(train_data['Close_diff'])\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:11:01.819234Z\",\"iopub.execute_input\":\"2021-12-11T03:11:01.819551Z\",\"iopub.status.idle\":\"2021-12-11T03:11:02.342305Z\",\"shell.execute_reply.started\":\"2021-12-11T03:11:01.819519Z\",\"shell.execute_reply\":\"2021-12-11T03:11:02.341258Z\"}}\n#Use to determine AR and MA parameters\nplt = sm.graphics.tsa.plot_pacf(train_data['Close_diff'], lags=40, zero=False)\nplt = sm.graphics.tsa.plot_acf(train_data['Close_diff'], lags=40, zero=False)\nplt.show()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:06.706621Z\",\"iopub.status.idle\":\"2021-12-11T03:08:06.707567Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:06.707218Z\",\"shell.execute_reply\":\"2021-12-11T03:08:06.707254Z\"}}\nmodel = auto_arima(train_data['Close'], start_p=0, start_q=0,\n                      test='adf',       \n                      max_p=3, max_q=3, \n                      m=1,              \n                      d=None,           \n                      seasonal=False,\n                      start_P=0, \n                      D=0, \n                      trace=True,\n                      error_action='ignore',  \n                      suppress_warnings=True, \n                      stepwise=True)\n\nprint(model.summary())\nplt.show()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:06.709466Z\",\"iopub.status.idle\":\"2021-12-11T03:08:06.72005Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:06.719545Z\",\"shell.execute_reply\":\"2021-12-11T03:08:06.719604Z\"}}\n# Build Model\nmodel = ARIMA(train_data['Close'], order=(1, 1, 1))  \nfitted = model.fit(disp=-1)  \n\n# Forecast with 95 % confidence interval\nfc, se, confidence = fitted.forecast(len(test_data['Close']), alpha=0.05)\n\n\n# Make as pandas series\nforecast_series = pd.Series(fc, index=test_data['Close'].index)\nlower_bound = pd.Series(conf[:, 0], index=test_data.index)\nupper_bound = pd.Series(conf[:, 1], index=test_data.index)\n\n# Plot\nplt2.figure(figsize=(12,5), dpi=100)\nplt2.plot(train_data['Close'], label='training')\nplt2.plot(test_data['Close'], label='testing')\nplt2.plot(forecast_series, label='forecast')\nplt2.fill_between(lower_bound.index, lower_bound, upper_bound, \n                 color='b', alpha=.15)\nplt2.title('Forecast vs Actual')\nplt2.legend(loc='upper left', fontsize=8)\nplt2.show()\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2021-12-11T03:08:06.72223Z\",\"iopub.status.idle\":\"2021-12-11T03:08:06.723212Z\",\"shell.execute_reply.started\":\"2021-12-11T03:08:06.722862Z\",\"shell.execute_reply\":\"2021-12-11T03:08:06.722907Z\"}}\nfrom sklearn.metrics import mean_squared_error, mean_absolute_error\nimport math\n\n# Performance metrics\nmse = mean_squared_error(test_data['Close'], fc)\nprint('Mean Squared Error: '+str(mse))\nmae = mean_absolute_error(test_data['Close'], fc)\nprint('Mean Absolute Error: '+str(mae))\nrmse = math.sqrt(mean_squared_error(test_data['Close'], fc))\nprint('Root Mean Ssquared Error: '+str(rmse))\nmape = np.mean(np.abs(fc - test_data['Close'])/np.abs(test_data['Close']))\nprint('Mean Absolute Percentage Error: '+str(mape))","metadata":{"_uuid":"f4c3e474-87b3-4ce2-ace5-65c9f7e27e7a","_cell_guid":"1c9c55d4-bca7-4529-9fca-0b1961240392","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]}]}